                        <form action="">
                            <button class="bg-[#4E73DF]  flex items-center text-white w-[300px]">
                                <div class="border border white mr-2 p-4">A</div>
                                <div class="p-2">Answerr</div>
                            </button>
                        </form>
                    @endfor
